import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';


function PostControlPanel(props) {
    return (
    <div>
      <button onClick={props.addPost}>Add Post</button>
      <button onClick={props.removePost}>Remove Post</button>
    </div>
    );
}

function AddPostPanel(props) {
  const [isSelectingImage, setIsSelectingImage] = useState(false);

  function uploadImage() { 
    console.log("Upload Image"); 
    setIsSelectingImage(true);
  }

  function submitPost() { 
    console.log("Submit Post"); 
    setIsAddingPost(false);
  }

  return (
    <div>
      {isSelectingImage && <input type="file" id="selectdFile" ref="fileUploader"></input>}
      <span className="Add-Post">Title:</span>
      <input></input>
      <span className="Add-Post">Content:</span>
      <textarea placeholder="Please input Content."></textarea>
      <div className="Add-Post">
        <button onClick={uploadImage}>upload</button>
        <button onClick={submitPost}>submit</button>
      </div>
    </div>
    );
}

function App() {
  const [isAddingPost, setIsAddingPost] = useState(false);

  function addPost() {
    console.log("Add Post");
    setIsAddingPost(true);
  }

  function removePost() { console.log("Remove Post"); }

  return (
    <div className="App"> 
    {
    isAddingPost
    ? <AddPostPanel />
    : <PostControlPanel addPost={addPost} removePost={removePost}/>
    }

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
