import React from 'react';
import logo from './logo.svg';
import './App.css';


function PostControlPanel(props) {
    return (
    <div>
      <button onClick={props.addPost}>Add Post</button>
      <button onClick={props.removePost}>Remove Post</button>
    </div>
    );
}

function AddPostPanel(props) {
  return (
    <div text-align = "center">
      <span>Title:</span>
      <span>Content:</span>
      <button>upload</button>
      <button>submit</button>
    </div>
    );
}

function App() {
  function addPost() { console.log("Add Post"); }
  function removePost() { console.log("Remove Post"); }
  return (
    <div className="App">
      <PostControlPanel addPost={addPost} removePost={removePost}/>
      <AddPostPanel />
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
